import firebase from 'firebase'
import React, {Component} from "react";

import '../stylesheets/Login.css'
import '../stylesheets/util.css'
import App from '../containers/App'
import setupSocket from '../sockets/index'

const auth = firebase.auth
const FBprovider = new auth.FacebookAuthProvider()
const Googleprovider = new auth.GoogleAuthProvider()

// Auth state will be cleared when the window or activity is refreshed
// Meaning users have to log in every time
auth().setPersistence(firebase.auth.Auth.Persistence.NONE)

// Logouts user from Auth (Firebase) then reloads page
export const logoutUser = () => {
    auth().signOut().then(() => {
        document.location.reload()
    })
}

export default class Login extends Component {

    state = {
        user: auth().currentUser
    };

    // Login using entered credentials
    loginCred = (name, pass) => {
        auth().signInWithEmailAndPassword(name, pass)
            .then(({user}) => {
                // Authentication success, no error
                this.setState({user})

            }, null)
            .then(null,
                // Firebase auth throws any error
                function (error) {
                    alert(error.code);
                    document.getElementById('use').value = ''
                    document.getElementById('pass').value = ''
                })
    };

    loginFB = () => {
        auth().signInWithPopup(FBprovider)
            .then(({user}) => {
                this.setState({user})
            })
    };

    loginGoogle = () => {
        auth().signInWithPopup(Googleprovider)
            .then(({user}) => {
                this.setState({user})
            })
    };

    render() {
        const {user} = this.state;
        if (user) {
            // User is signed in.
            setupSocket(user);
            return <div className="chat">
                <App user={user}/>
            </div>;
        } else {
            // No user is signed in.
            return (<div className='fireBaseApp'>
                <div id="full" className="container-login100">
                    <div className="wrap-login100 p-l-110 p-r-110 p-t-62 p-b-8">
                        <form className="login100-form validate-form flex-sb flex-w">
					<span className="login100-form-title p-b-8 m-b-30">
						Sign In With
					</span>
                            <a onClick={this.loginFB} className="btn-face m-b-20">
                                <i className="fa fa-facebook-official"></i>
                                Facebook
                            </a>
                            <a onClick={this.loginGoogle} className="btn-google m-b-20">
                                <img
                                    src="https://vignette.wikia.nocookie.net/polandball/images/5/51/Google.png/revision/latest?cb=20171210134732"
                                    alt="GOOGLE"/>
                                Google
                            </a>
                            <div className="p-t-31 p-b-9">
						<span className="txt1">
							Username
						</span>
                            </div>
                            <div className="wrap-input100 validate-input">
                                <input id="use" className="input100" type="text" name="username"/>
                                <span className="focus-input100"></span>
                            </div>

                            <div className="p-t-13 p-b-9">
						<span className="txt1">
							Password
						</span>
                            </div>
                            <div className="wrap-input100 validate-input">
                                <input id="pass" className="input100" type="password" name="pass"
                                       onKeyPress={(e) => {
                                           if (e.key === 'Enter') {
                                               this.loginCred(document.getElementById('use').value,
                                                   document.getElementById('pass').value)
                                           }
                                       }}/>
                                <span className="focus-input100"></span>
                            </div>
                            <div className="container-login100-form-btn m-t-17">
                                <a className="btn-google m-b-20" onClick={() =>
                                    this.loginCred(document.getElementById('use').value,
                                        document.getElementById('pass').value)
                                }>Sign in</a>
                            </div>
                            <span className="login100-form-footer b-t-26">
                            <img id="fire-logo"
                                 src="https://firebase.google.com/downloads/brand-guidelines/PNG/logo-built_white.png"
                                 alt="fire"/>
                                </span>
                        </form>
                    </div>
                </div>
            </div>);
        }
    }
}
